    /**
     * @brief Return available countries
     *
     * @param int $idLang Language ID
     * @param bool $active return only active coutries
     * @param bool $containStates return only country with states
     * @param bool $listStates Include the states list with the returned list
     *
     * @return array Countries iso codes
     */
    public static function getCountriesIso($active = true)
    {
        $sql = new DbQuery();
        $sql->select('id_country, LOWER(iso_code) as iso');
        $sql->from('country');
        $sql->where('active = '.(int)$active);
        $result = Db::getInstance(_PS_USE_SQL_SLAVE_)->executeS($sql);
        
        $countries = array();
        if (isset($result) && $result) {
            foreach ($result as $row) {
                $countries[$row['id_country']] = $row['iso'];
            }
        }
        return $countries;
    }

